import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { graphcms, QUERY_ONE_POST, options } from '../../graphql/Queries';
import './albumreview.css';

const AlbumReview = () => {
  const { slug } = useParams();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    graphcms.request(QUERY_ONE_POST, { slug })
    .then(res => setPosts(res.posts))
  }, [slug]);

  return (
    <article>
      {posts.length > 0 && 
        <header className='post-header'>
          <div className='main-post-info'>
            <Link to={`/${posts[0].category.slug}`}>
              <p className='category'>{posts[0].category.name}</p>
            </Link>
            <h1>{posts[0].title}</h1>
            <h2>{posts[0].artist}</h2>
            <p>{posts[0].year}</p>
          </div>
          <img src={posts[0].coverPhoto.url}/>
        </header>
      }
      <div className='divider'></div>

      {posts.length > 0 && 
        <div className='subtitle'>
          <p>{posts[0].datePublished}</p>
          <p>{posts[0].genre}</p>
          <p>{posts[0].label}</p>
        </div>
      }
    </article>
  )
}

export default AlbumReview
