import React, { useState, useEffect } from 'react'
import { useParams, Link } from 'react-router-dom';
import { graphcms, QUERY_SEARCH, options } from '../../graphql/Queries';
import Search from '../../components/Search';
import './searchpage.css';
import Release from '../../components/Release';
import { useMyContext } from '../../context/store';
import Pagination from '../../components/Pagination';

const SearchPage = () => {
  const { slug } = useParams();
  const { search } = useLocation();
  const page = Number(new URLSearchParams(search).get('page')) || 1;
  const skip = (page - 1) * 3;

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);
  const { setLoading } = useMyContext();
  const { totalPage } = useMyContext();

  useEffect(() => {
    setLoading(true);
    if (slug) {
      graphcms.request(QUERY_SEARCH, { slug }, 3)
      .then(res => {
        setPosts(res.posts)
        setError(null)
      })
      .catch(error => {
        setPosts([]);
        setError(error.message);
      })
      .finally(() => setLoading(false))
    }
    else {
      setPosts([]);
      setError(null);
      setLoading(false);
    }
  }, [setLoading, slug]);

  if(error) return <h2>{error}</h2>
  return (
    <div className='search-container'>
      <Search/>

      {slug && 
        <h2 className='search-results'>
          Blog posts for "{slug.replace(/-/g, ' ')}"
        </h2>
      }

      <div className='albums-grid'>
        {posts?.map(post => (
          <Link to={`/${post.category.slug}/${post.slug}`}>
            <Release 
              img={post.coverPhoto.url}
              name={post.title}
              artist={post.artist}
              date={new Date(post.datePublished).toLocaleDateString('en-US', options)}
              category={post.category.name}
            />
          </Link>
        ))}
      </div>

      <Pagination totalPage={totalPage}/>
    </div>
  )
}

export default SearchPage
