import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { graphcms } from '../graphql/Queries';
import { useMyContext } from '../context/store';

const useQueryPosts = ({query, limit = 1 }) => {
  const { slug } = useParams();
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const { setLoading, setTotalPage } = useMyContext();

  useEffect(() => {
    setLoading(true);

    graphcms.request(query, { slug, limit })
    .then(res => {
      const count = res?.countConnection?.aggregate?.count || 0;
      setTotalPage(Math.ceil(count / limit));
      console.log(count/limit);
      setPosts(res.posts);
      setError(null);
    })
    .catch(error => {
      setTotalPage(1);
      setPosts([]);
      setError(error.message);
    })
    .finally(() => setLoading(false))
  }, [setLoading, setTotalPage, query, slug, limit])

  return { posts, error }
}

export default useQueryPosts
