import { GraphQLClient, gql } from "graphql-request";

export const graphcms = new GraphQLClient(process.env.REACT_APP_API);

const category = `
  id,
  name,
  slug
`;

const comment = `
  id,
  name,
  comment
`;

const post = `
  id,
  title,
  slug,
  coverPhoto { url },
  content { html },
  datePublished,
  artist,
  label,
  rating,
  year,
  category {
    slug
  }
`;

// unused
export const QUERY_SLUG_CATEGORIES = gql`
{
  categories() {
    name,
    slug
  }
}
`;

export const ALBUM_POSTS = gql`
  {
    posts(where: {category: {name: "Albums"}}, orderBy: datePublished_DESC, first: 4) {
      ${post}
    }
  }
`;

export const ALBUM_POSTS_ALL = gql`
  {
    posts(where: {category: {name: "albums"}}, orderBy: datePublished_DESC) {
      ${post}
    }
  }
`;

export const QUERY_SEARCH = gql`
  query GetPostsByCategory($slug: String!) {
    posts(
      orderBy: updatedAt_DESC,
      where: {_search: $slug, AND: {slug_contains: $slug}}
    ) {
      ${post}
    }
  }
`;

export const QUERY_ONE_POST = gql`
  query GetOnePost($slug: String!) {
    posts(where: {slug: $slug}) {
      ${post}
    }
  }
`;
