import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './nav.css';

import { graphcms, QUERY_SLUG_CATEGORIES } from '../graphql/Queries';

const Nav = () => {
  const [visible, setVisible] = useState(false);

  function toggleNav() {
    setVisible(!visible);
  }

  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }
    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const [categories, setCategories] = useState([]);
  return (
    <nav className={scrolled ? "primary-scrolled" : "primary"}>
      <Link to="/" className="logo"></Link>

      <button
        onClick={toggleNav}
        className="mobile-nav-toggle"
        aria-controls="primary-navigation"
        aria-expanded={visible}
      >
        <span className="sr-only">Menu</span>
      </button>

      <ul id="primary-navigation" className={`primary-navigation ${visible ? 'visible' : ''}`}>
        <Link to="/albums" className='test'>
          <li>
            ALBUMS
          </li>
        </Link>
        <Link to="/feed" className='test'>
          <li>
            TRACKS
          </li>
        </Link>
        <Link to="/account" className='test'>
          <li>
            SEARCH
          </li>
        </Link>
      </ul>
    </nav>
  );
};

export default Nav;
