import { GraphQLClient, gql } from "graphql-request";

export const graphcms = new GraphQLClient(process.env.REACT_APP_API);

const category = `
  id,
  name,
  slug
`;

const comment = `
  id,
  name,
  comment
`;

const post = `
  id,
  title,
  slug,
  coverPhoto { url },
  content { html },
  datePublished,
  artist,
  label,
  rating,
  year,
`;

// unused
export const QUERY_SLUG_CATEGORIES = gql`
{
  categories() {
    name,
    slug
  }
}
`;

export const ALBUM_POSTS = gql`
  {
    posts(where: {category: {name: "Album"}}, orderBy: datePublished_ASC, first: 4) {
      ${post}
    }
  }
`;
