import { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { graphcms } from '../graphql/Queries';
import { useMyContext } from '../context/store';

const useQueryPosts = ({query, limit = 3 }) => {
  const { slug } = useParams();
  const { search } = useLocation();

  const page = Number(new URLSearchParams(search).get('page')) || 1;
  const skip = (page - 1) * limit;
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const { setLoading, setTotalPage } = useMyContext();
  console.log(skip);
  useEffect(() => {
    setLoading(true);
    if(slug) {
      graphcms.request(query, { slug, limit, skip })
      .then(res => {
        const count = res?.countConnection?.aggregate?.count || 0;
        setTotalPage(Math.ceil(count / limit));
        setPosts(res.posts);
        setError(null);
      })
      .catch(error => {
        setTotalPage(1);
        setPosts([]);
        setError(error.message);
      })
      .finally(() => setLoading(false))
    } else {
      setPosts([]);
      setError(null);
      setLoading(false);
    }
  }, [setLoading, setTotalPage, query, slug, limit, skip])

  return { posts, error }
}

export default useQueryPosts
