  return (
    <div className='comments'>
      <h2>A Penny for your Thoughts</h2>

      <form>
        <input 
          type="text" 
          required 
          placeholder='Name'
          value={name}
          onChange={e => setName(e.target.value)}
        />

        <input 
          type="email" 
          required 
          placeholder='Email'
          value={name}
          onChange={e => setEmail(e.target.value)}
        />  

        <textarea 
          cols="30" 
          rows="5" 
          placeholder='Comment'
          requiredvalue={comment}
          onChange={e => setComment(e.target.value)}
        />
        <button> Post</button>
      </form>
    </div>
  )
}

export default Comments
