import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useLocation } from "react-router-dom";
import Layout from "./pages/Layout";
import Landing from "./pages/landing/Landing";
import Category from "./pages/albums/Category";
import AlbumReview from "./pages/albums/Review";
import SearchPage from "./pages/search/SearchPage";
function App() {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Landing />} />
          <Route path=":slug" element={<Category />} />
          <Route path="/albums/:slug" element={<AlbumReview />} />
          <Route path="/tracks/:slug" element={<AlbumReview />} />
          <Route path="/search/:slug?" element={<SearchPage />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
